
module.exports.getCountryCode = function(countryName) {
	var code;

	switch(countryName){

		case "India" : 
			code = 91;
			break;
		case "USA" :
			code = 001;
			break;
		default:
			code = 0;

	}

	return code;
};

exports.subtract = function(n1, n2) {
	return n1 - n2;
};

